import React, { useEffect, useState } from "react";
import styled from "styled-components";
import { Typography, useTheme, useMediaQuery } from "@mui/material";

const ParallaxContainer = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  transform: translate3d(0, ${(props) => props.offset}px, 0);
`;

const ParallaxText = ({ children, baseVelocity, scrollPosition }) => {
  const theme = useTheme();
  const isSmallScreen = useMediaQuery(theme.breakpoints.down("sm"));

  return (
    <ParallaxContainer offset={(scrollPosition / 100) * baseVelocity}>
      <Typography
        color={"#FFF"}
        fontWeight={"bold"}
        variant="h1"
        style={{
          fontSize: isSmallScreen ? "3em" : "5.5rem",
          textAlign: "center",
        }}
      >
        {children}
      </Typography>
    </ParallaxContainer>
  );
};

const ProSkill = ({ scrollPosition }) => {
  return (
    <section style={{ paddingTop: "10rem" }}>
      <ParallaxText scrollPosition={scrollPosition} baseVelocity={-10}>
        LET'S
      </ParallaxText>
      <ParallaxText scrollPosition={scrollPosition} baseVelocity={10}>
        START
      </ParallaxText>
    </section>
  );
};

export default ProSkill;
