import React, { useEffect, useState } from "react";
import { Element, animateScroll as scroll } from "react-scroll";

const Main = ({ children }) => {
  const [currentSection, setCurrentSection] = useState(0);

  const handleScroll = () => {
    const elementHeight = window.innerHeight;
    const scrollPosition = window.scrollY;
    const newSection = Math.floor(scrollPosition / elementHeight);
    setCurrentSection(newSection);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const scrollToSection = (index) => {
    scroll.scrollTo(index * window.innerHeight);
  };

  return (
    <main
      id="main"
      role="main"
      style={{
        display: "flex",
        flexDirection: "column",
        height: "100vh",
        overflowY: "scroll",
      }}
    >
      {React.Children.map(children, (child, index) => (
        <Element key={index} name={`section${index}`} className="element">
          <div
            style={{
              flex: "1",
              minHeight: "100vh",
              display: "flex",
              flexDirection: "row",
              transition: "transform 0.5s ease-in-out",
              transform: `translateY(${(index - currentSection) * 100}vh)`,
            }}
          >
            {child}
          </div>
        </Element>
      ))}
    </main>
  );
};

export default Main;
